sp_widget.client_script:
function($rootScope, $scope) {
	/* widget controller */
	var c = this;
	console.log("c", c);
	console.log("c.data", c.data);
		console.log("c.data.getUserDisplayName", c.data.getUserDisplayName);

	//	console.log("data.noteGR:", c.data.noteGR);
	// console.log("data.test:", c.data.test);

	c.selectItem = function(idx) {
		console.log("Notes List: rootScope", $rootScope);
		console.log("Notes List: scope", $scope);

		var id = c.data.notes[idx].sys_id;
		console.log('Notes List Note ID:', id);

		$rootScope.noteID = id;
		console.log("Notes List: $rootScope after", $rootScope);
		$rootScope.$emit('selectNote',id);

		console.log("");
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	// create an array to populate with notes
	// data.test = "test";
	// data.noteGR = noteGR;

	data.notes = [];

	var noteGR = new GlideRecord('x_snc_createnotes_note');

	noteGR.addQuery('user', gs.getUser().getID());

	data.test = "test";
	data.getUserDisplayName = gs.getUserDisplayName();

	data.getID = gs.getUser().getID();

	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while (noteGR.next()) {
		var noteObj = {};

		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number, title, sys_id');

		// get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0, 20)
		data.notes.push(noteObj);
	}

})();
